import { Component } from 'react';
import FeedbackOptions from './FeedbackOptions';
import Statistics from './Statistics';
import Section from './Section';
import Notification from './Notification';

export class App extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  setOption = option => {
    this.setState(prevState => ({ [option]: prevState[option] + 1 }));
  };

  countTotalFeedback = () => {
    const options = Object.values(this.state);
    return options.reduce(
      (total, option) => (option.completed ? total + 1 : total),
      0
    );
  };

  render() {
    const feedbacks = Object.keys(this.state);
    const countTotalFeedback = this.countTotalFeedback();
    const countPositiveFeedbackPercentage = Math.round(
      (this.state.good * 100) / countTotalFeedback
    );
    return (
      <>
        <Section text="Please leave headback">
          <FeedbackOptions
            options={feedbacks}
            onLeaveFeedback={this.setOption}
            // onGood={this.goodIncrement}
            // onNeutral={this.neutralIncrement}
            // onBad={this.badIncrement}
          />
        </Section>
        {countTotalFeedback > 0 ? (
          <Section text="Statistics">
            <Statistics
              good={this.state.good}
              neutral={this.state.neutral}
              bad={this.state.bad}
              total={countTotalFeedback}
              positivePercentage={countPositiveFeedbackPercentage}
            />
          </Section>
        ) : (
          <Notification message="There is no feedback" />
        )}
      </>
    );
  }
}
